import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import statistiqueService from '../../services/statistique.service';
import Spinner from '../../components/common/Spinner';
import { useNotification } from '../../contexts/NotificationContext';

const ActiviteStatDetails = () => {
  const { id, dateDebut, dateFin } = useParams();
  const [statistiques, setStatistiques] = useState(null);
  const [loading, setLoading] = useState(true);
  const { showError } = useNotification();

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        // Décoder les dates qui sont encodées dans l'URL
        const decodedDateDebut = decodeURIComponent(dateDebut);
        const decodedDateFin = decodeURIComponent(dateFin);

        const data = await statistiqueService.getStatistiquesDetailsByActivite(
          id, 
          decodedDateDebut, 
          decodedDateFin
        );

        setStatistiques(data);
        setLoading(false);
      } catch (err) {
        console.error('Erreur lors du chargement des statistiques détaillées:', err);
        showError(err.response?.data?.error || 'Erreur lors du chargement des statistiques détaillées. Veuillez réessayer plus tard.');
        setLoading(false);
      }
    };

    fetchData();
  }, [id, dateDebut, dateFin, showError]);

  if (loading) return <Spinner text="Chargement des détails de l'activité..." />;

  if (!statistiques) {
    return (
      <div className="container-fluid mt-4">
        <div className="alert alert-warning">
          Aucune donnée disponible pour cette activité dans la période spécifiée.
          <Link to="/statistiques/activites-membres" className="ms-2">Retour aux statistiques</Link>
        </div>
      </div>
    );
  }

  return (
    <div className="container-fluid mt-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Détails de l'activité: {statistiques.description}</h2>
        <div>
          <Link to="/statistiques/activites-membres" className="btn btn-secondary me-2">
            <i className="bi bi-arrow-left me-2"></i>
            Retour aux statistiques
          </Link>
          <Link to={`/activites/${id}`} className="btn btn-primary">
            <i className="bi bi-eye me-2"></i>
            Voir l'activité
          </Link>
        </div>
      </div>

      <div className="alert alert-info">
        <strong>Période:</strong> Du {new Date(decodeURIComponent(dateDebut)).toLocaleDateString()} au {new Date(decodeURIComponent(dateFin)).toLocaleDateString()}
      </div>

      <div className="row mb-4">
        <div className="col-md-4">
          <div className="card mb-3">
            <div className="card-body">
              <h5 className="card-title">Informations générales</h5>
              <ul className="list-group list-group-flush">
                <li className="list-group-item d-flex justify-content-between align-items-center">
                  Date
                  <span>{new Date(statistiques.daty).toLocaleDateString()}</span>
                </li>
                <li className="list-group-item d-flex justify-content-between align-items-center">
                  Région
                  <span>{statistiques.region}</span>
                </li>
                <li className="list-group-item d-flex justify-content-between align-items-center">
                  Cotisation
                  <span>{statistiques.cotisation?.toLocaleString()} Ar</span>
                </li>
                <li className="list-group-item d-flex justify-content-between align-items-center">
                  Priorité
                  <span className={`badge bg-${getPriorityColor(statistiques.priorite)}`}>
                    {statistiques.priorite}/10
                  </span>
                </li>
              </ul>
            </div>
          </div>
        </div>
        <div className="col-md-8">
          <div className="row">
            <div className="col-md-4">
              <div className="card text-white bg-primary mb-3">
                <div className="card-body">
                  <h5 className="card-title">Participants</h5>
                  <p className="card-text display-4">{statistiques.nombreParticipants}</p>
                </div>
              </div>
            </div>
            <div className="col-md-4">
              <div className="card text-white bg-success mb-3">
                <div className="card-body">
                  <h5 className="card-title">Montant reçu</h5>
                  <p className="card-text display-4">{statistiques.montantRecu?.toLocaleString()} Ar</p>
                </div>
              </div>
            </div>
            <div className="col-md-4">
              <div className="card text-white bg-danger mb-3">
                <div className="card-body">
                  <h5 className="card-title">Reste à payer</h5>
                  <p className="card-text display-4">{statistiques.resteAPayer?.toLocaleString()} Ar</p>
                </div>
              </div>
            </div>
          </div>

          <div className="card mb-3">
            <div className="card-body">
              <h5 className="card-title">Taux de recouvrement</h5>
              <div className="progress" style={{ height: '30px' }}>
                <div 
                  className="progress-bar bg-success" 
                  role="progressbar" 
                  style={{ width: `${statistiques.tauxRecouvrement}%` }}
                  aria-valuenow={statistiques.tauxRecouvrement} 
                  aria-valuemin="0" 
                  aria-valuemax="100"
                >
                  {Math.round(statistiques.tauxRecouvrement)}%
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="card mb-4">
        <div className="card-body">
          <h5 className="card-title">Liste des participants</h5>
          <div className="table-responsive">
            <table className="table table-striped table-hover">
              <thead className="table-primary">
                <tr>
                  <th>Nom</th>
                  <th>Prénom</th>
                  <th>Type</th>
                  <th>Accompagnateur</th>
                  <th>Montant à payer</th>
                  <th>Montant payé</th>
                  <th>Reste à payer</th>
                  <th>Statut</th>
                </tr>
              </thead>
              <tbody>
                {statistiques.participants && statistiques.participants.map((participant) => {
                  const montantAPayer = parseFloat(participant.montantAPayer || 0);
                  const montantPaye = parseFloat(participant.montantPaye || 0);
                  const resteAPayer = Math.max(0, montantAPayer - montantPaye);
                  const estCompletementPaye = resteAPayer === 0;
                  const estPartiellementPaye = montantPaye > 0 && resteAPayer > 0;

                  return (
                    <tr key={participant.id}>
                      <td>{participant.nom}</td>
                      <td>{participant.prenom}</td>
                      <td>
                        <span className={`badge ${participant.est_membre ? 'bg-primary' : 'bg-secondary'}`}>
                          {participant.est_membre ? 'Membre' : 'Non-membre'}
                        </span>
                      </td>
                      <td>
                        {participant.accompagnateur ? 
                          `${participant.accompagnateur.nom} ${participant.accompagnateur.prenom}` : 
                          '-'}
                      </td>
                      <td>{montantAPayer.toLocaleString()} Ar</td>
                      <td className="text-success">{montantPaye.toLocaleString()} Ar</td>
                      <td className="text-danger">{resteAPayer.toLocaleString()} Ar</td>
                      <td>
                        {estCompletementPaye ? (
                          <span className="badge bg-success">Payé</span>
                        ) : estPartiellementPaye ? (
                          <span className="badge bg-warning">Partiel</span>
                        ) : (
                          <span className="badge bg-danger">Non payé</span>
                        )}
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        </div>
      </div>

      <div className="card mb-4">
        <div className="card-body">
          <h5 className="card-title">Historique des paiements</h5>
          <div className="table-responsive">
            <table className="table table-striped table-hover">
              <thead className="table-primary">
                <tr>
                  <th>Date</th>
                  <th>Payeur</th>
                  <th>Type</th>
                  <th>Montant</th>
                </tr>
              </thead>
              <tbody>
                {statistiques.paiements && statistiques.paiements.map((paiement) => (
                  <tr key={paiement.id}>
                    <td>{new Date(paiement.daty).toLocaleDateString()}</td>
                    <td>{paiement.nom} {paiement.prenom}</td>
                    <td>
                      <span className={`badge ${paiement.est_membre ? 'bg-primary' : 'bg-secondary'}`}>
                        {paiement.est_membre ? 'Membre' : 'Non-membre'}
                      </span>
                    </td>
                    <td className="text-success">{parseFloat(paiement.montant).toLocaleString()} Ar</td>
                  </tr>
                ))}
              </tbody>
              <tfoot className="table-secondary fw-bold">
                <tr>
                  <td colSpan="3">Total</td>
                  <td className="text-success">{statistiques.montantRecu?.toLocaleString()} Ar</td>
                </tr>
              </tfoot>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

// Fonction utilitaire pour déterminer la couleur en fonction de la priorité
const getPriorityColor = (priorite) => {
  const priority = parseInt(priorite);
  if (priority >= 8) return 'danger';
  if (priority >= 5) return 'warning';
  if (priority >= 3) return 'info';
  return 'success';
};

export default ActiviteStatDetails;
