import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import personneService from '../../services/personne.service';
import Spinner from '../../components/common/Spinner';
import Alert from '../../components/common/Alert';

const PersonnesList = () => {
  const [personnes, setPersonnes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  useEffect(() => {
    fetchPersonnes();
  }, []);

  const fetchPersonnes = async () => {
    try {
      setLoading(true);
      const data = await personneService.getAllPersonnes();
      setPersonnes(data);
      setLoading(false);
    } catch (err) {
      console.error('Erreur lors du chargement des personnes:', err);
      setError('Erreur lors du chargement des personnes. Veuillez réessayer plus tard.');
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('Êtes-vous sûr de vouloir supprimer cette personne ?')) {
      try {
        await personneService.deletePersonne(id);
        setPersonnes(personnes.filter(personne => personne.id !== id));
        setSuccessMessage('Personne supprimée avec succès');

        // Effacer le message de succès après 3 secondes
        setTimeout(() => {
          setSuccessMessage('');
        }, 3000);
      } catch (err) {
        console.error('Erreur lors de la suppression de la personne:', err);
        setError('Erreur lors de la suppression de la personne. Veuillez réessayer plus tard.');
      }
    }
  };

  const filteredPersonnes = personnes.filter(personne => {
    const fullName = `${personne.nom} ${personne.prenom}`.toLowerCase();
    return fullName.includes(searchTerm.toLowerCase());
  });

  if (loading) return <Spinner text="Chargement des personnes..." />;

  return (
    <div className="personnes-list">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h1>Liste des personnes</h1>
        <Link to="/personnes/new" className="btn btn-primary">
          <i className="bi bi-plus-circle me-2"></i>Ajouter une personne
        </Link>
      </div>

      {successMessage && (
        <Alert 
          type="success" 
          message={successMessage} 
          onClose={() => setSuccessMessage('')}
        />
      )}

      {error && (
        <Alert 
          type="danger" 
          message={error} 
          onClose={() => setError(null)}
        />
      )}

      <div className="card mb-4">
        <div className="card-body">
          <div className="input-group">
            <span className="input-group-text">
              <i className="bi bi-search"></i>
            </span>
            <input
              type="text"
              className="form-control"
              placeholder="Rechercher une personne..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            {searchTerm && (
              <button
                className="btn btn-outline-secondary"
                type="button"
                onClick={() => setSearchTerm('')}
              >
                <i className="bi bi-x"></i>
              </button>
            )}
          </div>
        </div>
      </div>

      {filteredPersonnes.length === 0 ? (
        <div className="alert alert-info">
          Aucune personne trouvée.
        </div>
      ) : (
        <div className="table-responsive">
          <table className="table table-striped table-hover">
            <thead className="table-light">
              <tr>
                <th>ID</th>
                <th>Nom</th>
                <th>Prénom</th>
                <th>Date de naissance</th>
                <th>Service Public</th>
                <th>Région</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredPersonnes.map(personne => (
                <tr key={personne.id}>
                  <td>{personne.id}</td>
                  <td>{personne.nom}</td>
                  <td>{personne.prenom}</td>
                  <td>{new Date(personne.dtn).toLocaleDateString()}</td>
                  <td>{personne.sp_description || '-'}</td>
                  <td>{personne.sp_region || '-'}</td>
                  <td>
                    <div className="btn-group btn-group-sm">
                      <Link 
                        to={`/personnes/edit/${personne.id}`} 
                        className="btn btn-outline-primary"
                        title="Modifier"
                      >
                        <i className="bi bi-pencil"></i>
                      </Link>
                      <button
                        className="btn btn-outline-danger"
                        onClick={() => handleDelete(personne.id)}
                        title="Supprimer"
                      >
                        <i className="bi bi-trash"></i>
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default PersonnesList;
