import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { useNotification } from '../../contexts/NotificationContext';
import paiementService from '../../services/paiement.service';
import activiteService from '../../services/activite.service';
import membreService from '../../services/membre.service';
import presenceService from '../../services/presence.service';
import personneService from '../../services/personne.service';
import './PaiementForm.css';

const PaiementForm = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { showSuccess, showError } = useNotification();
  const isEditMode = !!id;

  const [loading, setLoading] = useState(false);
  const [activites, setActivites] = useState([]);
  const [membres, setMembres] = useState([]);
  const [membrePresences, setMembrePresences] = useState([]);
  const [selectedActivite, setSelectedActivite] = useState('');
  const [selectedMembre, setSelectedMembre] = useState('');
  const [formData, setFormData] = useState({
    id_presence_act: '',
    daty: new Date().toISOString().split('T')[0],
    montant: '',
    nbPersonnes: 0
  });

  // Paramètres de remise (à gérer uniquement dans le code)
  const [nbPersonnesRemise, setNbPersonnesRemise] = useState(3);
  const [pourcentageRemise, setPourcentageRemise] = useState(10);

  // Montant calculé avec remise éventuelle
  const [montantAvecRemise, setMontantAvecRemise] = useState(0);

  useEffect(() => {
    const fetchActivites = async () => {
      try {
        const data = await activiteService.getAllActivites();
        setActivites(data);
      } catch (error) {
        console.error('Erreur lors du chargement des activités:', error);
        showError('Erreur lors du chargement des activités');
      }
    };

    const fetchMembres = async () => {
      try {
        const data = await membreService.getAllMembres();
        setMembres(data);
      } catch (error) {
        console.error('Erreur lors du chargement des membres:', error);
        showError('Erreur lors du chargement des membres');
      }
    };

    fetchActivites();
    fetchMembres();

    if (isEditMode) {
      const fetchPaiement = async () => {
        try {
          setLoading(true);
          const data = await paiementService.getPaiementById(id);
          setFormData({
            id_presence_act: data.id_presence_act,
            daty: new Date(data.daty).toISOString().split('T')[0],
            montant: data.montant,
            nbPersonnes: 0
          });
          setSelectedActivite(data.id_act);

          // Récupérer la présence pour obtenir le membre
          const presenceData = await presenceService.getPresenceById(data.id_presence_act);
          if (presenceData && presenceData.id_membre) {
            setSelectedMembre(presenceData.id_membre);
          }

          setLoading(false);
        } catch (error) {
          console.error('Erreur lors du chargement du paiement:', error);
          showError('Erreur lors du chargement du paiement');
          setLoading(false);
        }
      };

      fetchPaiement();
    }
  }, [id, isEditMode, showError]);

  useEffect(() => {
    if (selectedActivite && selectedMembre) {
      const fetchMembrePresences = async () => {
        try {
          // Récupérer les présences du membre pour cette activité
          const data = await presenceService.getPresencesByActivite(selectedActivite);
          const membrePresencesData = data.filter(p => p.id_membre === parseInt(selectedMembre));
          setMembrePresences(membrePresencesData);

          // Si une seule présence est trouvée, la sélectionner automatiquement
          if (membrePresencesData.length === 1) {
            setFormData(prevFormData => ({
              ...prevFormData,
              id_presence_act: membrePresencesData[0].id.toString()
            }));
          }
        } catch (error) {
          console.error('Erreur lors du chargement des présences du membre:', error);
          showError('Erreur lors du chargement des présences du membre');
        }
      };

      fetchMembrePresences();
    } else {
      setMembrePresences([]);
    }
  }, [selectedActivite, selectedMembre, showError]);

  // Effet pour calculer le montant avec remise
  useEffect(() => {
    let montant = parseFloat(formData.montant) || 0;

    // Considérer les personnes emmenées comme des participants non-membres
    // Le nombre total de participants est donc 1 (le membre) + le nombre de personnes emmenées
    const totalParticipants = 1 + parseInt(formData.nbPersonnes || 0);

    // Appliquer la remise si le nombre total de participants est suffisant
    if (totalParticipants >= nbPersonnesRemise) {
      const remise = (montant * pourcentageRemise) / 100;
      montant = montant - remise;
    }

    setMontantAvecRemise(montant);
  }, [formData.montant, formData.nbPersonnes, nbPersonnesRemise, pourcentageRemise]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleActiviteChange = (e) => {
    setSelectedActivite(e.target.value);
    setFormData({
      ...formData,
      id_presence_act: ''
    });
  };

  const handleMembreChange = (e) => {
    setSelectedMembre(e.target.value);
    setFormData({
      ...formData,
      id_presence_act: ''
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!selectedMembre) {
      showError('Veuillez sélectionner un membre');
      return;
    }

    if (membrePresences.length === 0) {
      showError('Ce membre n\'est pas inscrit à cette activité. Veuillez d\'abord l\'inscrire avant de procéder au paiement.');
      return;
    }

    // Si le membre est inscrit, utiliser sa présence
    const id_presence_act = membrePresences[0].id;
    const nbPersonnesAjoutees = parseInt(formData.nbPersonnes || 0);

    try {
      setLoading(true);

      // Préparer les données de paiement (sans le nbPersonnes qui est juste pour le calcul)
      const paiementData = {
        id_presence_act: id_presence_act,
        daty: formData.daty,
        montant: montantAvecRemise // Utiliser le montant avec remise
      };

      // Créer le paiement principal
      if (isEditMode) {
        await paiementService.updatePaiement(id, paiementData);
        showSuccess('Paiement mis à jour avec succès');
      } else {
        // C'est un membre, donc utiliser createPaiementMembre
        await paiementService.createPaiementMembre(paiementData);

        // Si des personnes non-membres sont ajoutées, les créer et les ajouter comme participants
        if (nbPersonnesAjoutees > 0) {
          // Récupérer les informations du membre pour les utiliser dans la création des personnes
          const membre = membres.find(m => m.id.toString() === selectedMembre);

          // Créer et ajouter chaque personne non-membre
          for (let i = 0; i < nbPersonnesAjoutees; i++) {
            try {
              // 1. Créer une nouvelle personne (non-membre)
              const personneData = {
                nom: `Invité de ${membre.nom}`,
                prenom: `${i + 1}`,
                adresse: membre.adresse || '',
                telephone: membre.telephone || ''
              };

              const nouvellePersonne = await personneService.createPersonne(personneData);

              // 2. Ajouter cette personne comme participant à l'activité avec le membre comme accompagnateur
              try {
                await presenceService.createPresencePersonne({
                  id_personne: nouvellePersonne.id,
                  id_act: selectedActivite,
                  id_membre_accompagnateur: membre.id
                });
                console.log(`Personne non-membre ajoutée avec succès: ${nouvellePersonne.id}`);
              } catch (presenceError) {
                console.error('Erreur lors de l\'ajout de la présence:', presenceError.response?.data || presenceError.message);
                throw presenceError;
              }
            } catch (err) {
              console.error(`Erreur lors de l'ajout de la personne non-membre ${i + 1}:`, err);
              // Continuer avec les autres personnes même si une échoue
            }
          }
        }

        showSuccess(`Paiement créé avec succès${nbPersonnesAjoutees > 0 ? ` et ${nbPersonnesAjoutees} personne(s) non-membre(s) ajoutée(s)` : ''}`);
      }

      // Rediriger vers la page de détails de l'activité au lieu de la liste des paiements
      navigate(`/activites/${selectedActivite}`);
    } catch (error) {
      console.error('Erreur lors de l\'enregistrement du paiement:', error);
      showError('Erreur lors de l\'enregistrement du paiement');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="d-flex justify-content-center">
        <div className="spinner-border" role="status">
          <span className="visually-hidden">Chargement...</span>
        </div>
      </div>
    );
  }

  return (
    <div className="paiement-form">
      <h1>{isEditMode ? 'Modifier le paiement' : 'Nouveau paiement'}</h1>

      <form onSubmit={handleSubmit} className="card">
        <div className="card-body">
          <div className="mb-3">
            <label htmlFor="activite" className="form-label">Activité</label>
            <select
              id="activite"
              className="form-select"
              value={selectedActivite}
              onChange={handleActiviteChange}
              disabled={isEditMode}
              required
            >
              <option value="">Sélectionner une activité</option>
              {activites.map(activite => (
                <option key={activite.id} value={activite.id}>
                  {new Date(activite.daty).toLocaleDateString()} - {activite.description} - {activite.cotisation} €
                </option>
              ))}
            </select>
          </div>

          <div className="mb-3">
            <label htmlFor="membre" className="form-label">Membre qui emmène des personnes</label>
            <select
              id="membre"
              className="form-select"
              value={selectedMembre}
              onChange={handleMembreChange}
              disabled={isEditMode || !selectedActivite}
              required
            >
              <option value="">Sélectionner un membre</option>
              {membres.map(membre => (
                <option key={membre.id} value={membre.id}>
                  {membre.nom} {membre.prenom}
                </option>
              ))}
            </select>
          </div>

          {membrePresences.length > 0 && (
            <div className="alert alert-info">
              <p><strong>Présence confirmée pour cette activité</strong></p>
              <p>Le membre {membres.find(m => m.id.toString() === selectedMembre)?.nom} {membres.find(m => m.id.toString() === selectedMembre)?.prenom} est bien inscrit à cette activité.</p>
            </div>
          )}

          {selectedActivite && selectedMembre && membrePresences.length === 0 && (
            <div className="alert alert-warning">
              <p><strong>Attention !</strong></p>
              <p>Ce membre n'est pas inscrit à cette activité. Veuillez d'abord l'inscrire à l'activité avant de procéder au paiement.</p>
            </div>
          )}

          <div className="mb-3">
            <label htmlFor="daty" className="form-label">Date de paiement</label>
            <input
              type="date"
              className="form-control"
              id="daty"
              name="daty"
              value={formData.daty}
              onChange={handleChange}
              required
            />
          </div>

          <div className="mb-3">
            <label htmlFor="montant" className="form-label">Montant</label>
            <input
              type="number"
              step="0.01"
              className="form-control"
              id="montant"
              name="montant"
              value={formData.montant}
              onChange={handleChange}
              required
            />
          </div>

          <div className="mb-3">
            <label htmlFor="nbPersonnes" className="form-label">Nombre de personnes non-membres amenées</label>
            <input
              type="number"
              min="0"
              className="form-control"
              id="nbPersonnes"
              name="nbPersonnes"
              value={formData.nbPersonnes}
              onChange={handleChange}
            />
            <small className="form-text text-muted">
              Ces personnes sont considérées comme des participants non-membres à l'activité.
            </small>
            <small className="form-text text-muted d-block">
              Une remise de {pourcentageRemise}% sera appliquée si le nombre total de participants (membre + personnes amenées) est supérieur ou égal à {nbPersonnesRemise}.
            </small>
          </div>

          <div className="mb-3">
            <label htmlFor="nbPersonnesRemise" className="form-label">Nombre de personnes pour remise</label>
            <input
              type="number"
              min="1"
              className="form-control"
              id="nbPersonnesRemise"
              value={nbPersonnesRemise}
              onChange={(e) => setNbPersonnesRemise(parseInt(e.target.value))}
            />
          </div>

          <div className="mb-3">
            <label htmlFor="pourcentageRemise" className="form-label">Pourcentage de remise (%)</label>
            <input
              type="number"
              min="0"
              max="100"
              step="0.1"
              className="form-control"
              id="pourcentageRemise"
              value={pourcentageRemise}
              onChange={(e) => setPourcentageRemise(parseFloat(e.target.value))}
            />
          </div>

          {formData.montant && (
            <div className="alert alert-info">
              <strong>Montant après remise éventuelle: {montantAvecRemise.toFixed(2)} Ar</strong>
              {parseInt(formData.nbPersonnes || 0) + 1 >= nbPersonnesRemise ? (
                <div>
                  <small>
                    <i className="bi bi-check-circle-fill me-1"></i>
                    Une remise de {pourcentageRemise}% a été appliquée car le nombre total de participants ({parseInt(formData.nbPersonnes || 0) + 1}) est supérieur ou égal à {nbPersonnesRemise}
                  </small>
                </div>
              ) : (
                <div>
                  <small>
                    <i className="bi bi-info-circle-fill me-1"></i>
                    Aucune remise appliquée. Il faut au moins {nbPersonnesRemise} participants pour bénéficier d'une remise de {pourcentageRemise}% (actuellement {parseInt(formData.nbPersonnes || 0) + 1} participants)
                  </small>
                </div>
              )}
            </div>
          )}

          <div className="d-flex justify-content-between">
            <button
              type="button"
              className="btn btn-secondary"
              onClick={() => navigate('/paiements')}
            >
              Annuler
            </button>
            <button
              type="submit"
              className="btn btn-primary"
              disabled={loading}
            >
              {isEditMode ? 'Mettre à jour' : 'Enregistrer'}
            </button>
          </div>
        </div>
      </form>
    </div>
  );
};

export default PaiementForm;
