import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import statistiqueService from '../../services/statistique.service';
import Spinner from '../../components/common/Spinner';
import { useNotification } from '../../contexts/NotificationContext';

const MembreStatDetails = () => {
  const { id, dateDebut, dateFin } = useParams();
  const [statistiques, setStatistiques] = useState(null);
  const [loading, setLoading] = useState(true);
  const { showError } = useNotification();

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        // Décoder les dates qui sont encodées dans l'URL
        const decodedDateDebut = decodeURIComponent(dateDebut);
        const decodedDateFin = decodeURIComponent(dateFin);

        const data = await statistiqueService.getStatistiquesDetailsByMembre(
          id, 
          decodedDateDebut, 
          decodedDateFin
        );

        setStatistiques(data);
        setLoading(false);
      } catch (err) {
        console.error('Erreur lors du chargement des statistiques détaillées:', err);
        showError(err.response?.data?.error || 'Erreur lors du chargement des statistiques détaillées. Veuillez réessayer plus tard.');
        setLoading(false);
      }
    };

    fetchData();
  }, [id, dateDebut, dateFin, showError]);

  if (loading) return <Spinner text="Chargement des détails du membre..." />;

  if (!statistiques) {
    return (
      <div className="container-fluid mt-4">
        <div className="alert alert-warning">
          Aucune donnée disponible pour ce membre dans la période spécifiée.
          <Link to="/statistiques/activites-personnes" className="ms-2">Retour aux statistiques</Link>
        </div>
      </div>
    );
  }

  return (
    <div className="container-fluid mt-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Détails du membre: {statistiques.nom} {statistiques.prenom}</h2>
        <div>
          <Link to="/statistiques/activites-personnes" className="btn btn-secondary me-2">
            <i className="bi bi-arrow-left me-2"></i>
            Retour aux statistiques
          </Link>
          <Link to={`/membres/${id}`} className="btn btn-primary">
            <i className="bi bi-eye me-2"></i>
            Voir le membre
          </Link>
        </div>
      </div>

      <div className="alert alert-info">
        <strong>Période:</strong> Du {new Date(decodeURIComponent(dateDebut)).toLocaleDateString()} au {new Date(decodeURIComponent(dateFin)).toLocaleDateString()}
      </div>

      <div className="row mb-4">
        <div className="col-md-3">
          <div className="card text-white bg-primary mb-3">
            <div className="card-body">
              <h5 className="card-title">Activités participées</h5>
              <p className="card-text display-4">{statistiques.nombreActivites}</p>
            </div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="card text-white bg-success mb-3">
            <div className="card-body">
              <h5 className="card-title">Personnes amenées</h5>
              <p className="card-text display-4">{statistiques.nombrePersonnesTierces}</p>
            </div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="card text-white bg-info mb-3">
            <div className="card-body">
              <h5 className="card-title">Montant total payé</h5>
              <p className="card-text display-4">{statistiques.montantRecu?.toLocaleString()} Ar</p>
            </div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="card text-white bg-danger mb-3">
            <div className="card-body">
              <h5 className="card-title">Reste à payer</h5>
              <p className="card-text display-4">{statistiques.resteAPayer?.toLocaleString()} Ar</p>
            </div>
          </div>
        </div>
      </div>

      <div className="card mb-4">
        <div className="card-body">
          <h5 className="card-title">Taux de paiement</h5>
          <div className="progress" style={{ height: '30px' }}>
            <div 
              className="progress-bar bg-success" 
              role="progressbar" 
              style={{ width: `${statistiques.tauxPaiement}%` }}
              aria-valuenow={statistiques.tauxPaiement} 
              aria-valuemin="0" 
              aria-valuemax="100"
            >
              {Math.round(statistiques.tauxPaiement)}%
            </div>
          </div>
        </div>
      </div>

      <div className="card mb-4">
        <div className="card-body">
          <h5 className="card-title">Activités participées</h5>
          <div className="table-responsive">
            <table className="table table-striped table-hover">
              <thead className="table-primary">
                <tr>
                  <th>Date</th>
                  <th>Description</th>
                  <th>Région</th>
                  <th>Cotisation</th>
                  <th>Montant payé</th>
                  <th>Reste à payer</th>
                  <th>Statut</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {statistiques.activites && statistiques.activites.map((activite) => {
                  const cotisation = parseFloat(activite.cotisation || 0);
                  const montantPaye = parseFloat(activite.montantPaye || 0);
                  const resteAPayer = Math.max(0, cotisation - montantPaye);
                  const estCompletementPaye = resteAPayer === 0;
                  const estPartiellementPaye = montantPaye > 0 && resteAPayer > 0;

                  return (
                    <tr key={activite.id}>
                      <td>{new Date(activite.daty).toLocaleDateString()}</td>
                      <td>{activite.description}</td>
                      <td>{activite.region}</td>
                      <td>{cotisation.toLocaleString()} Ar</td>
                      <td className="text-success">{montantPaye.toLocaleString()} Ar</td>
                      <td className="text-danger">{resteAPayer.toLocaleString()} Ar</td>
                      <td>
                        {estCompletementPaye ? (
                          <span className="badge bg-success">Payé</span>
                        ) : estPartiellementPaye ? (
                          <span className="badge bg-warning">Partiel</span>
                        ) : (
                          <span className="badge bg-danger">Non payé</span>
                        )}
                      </td>
                      <td>
                        <Link to={`/activites/${activite.id}`} className="btn btn-sm btn-primary">
                          <i className="bi bi-eye me-1"></i>Détails
                        </Link>
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        </div>
      </div>

      <div className="card mb-4">
        <div className="card-body">
          <h5 className="card-title">Personnes amenées</h5>
          <div className="table-responsive">
            <table className="table table-striped table-hover">
              <thead className="table-primary">
                <tr>
                  <th>Nom</th>
                  <th>Prénom</th>
                  <th>Activité</th>
                  <th>Date</th>
                  <th>Montant à payer</th>
                  <th>Montant payé</th>
                  <th>Reste à payer</th>
                  <th>Statut</th>
                </tr>
              </thead>
              <tbody>
                {statistiques.personnesTierces && statistiques.personnesTierces.map((personne) => {
                  const montantAPayer = parseFloat(personne.montantAPayer || 0);
                  const montantPaye = parseFloat(personne.montantPaye || 0);
                  const resteAPayer = Math.max(0, montantAPayer - montantPaye);
                  const estCompletementPaye = resteAPayer === 0;
                  const estPartiellementPaye = montantPaye > 0 && resteAPayer > 0;

                  return (
                    <tr key={`${personne.id_personne}-${personne.id_activite}`}>
                      <td>{personne.nom}</td>
                      <td>{personne.prenom}</td>
                      <td>{personne.description_activite}</td>
                      <td>{new Date(personne.date_activite).toLocaleDateString()}</td>
                      <td>{montantAPayer.toLocaleString()} Ar</td>
                      <td className="text-success">{montantPaye.toLocaleString()} Ar</td>
                      <td className="text-danger">{resteAPayer.toLocaleString()} Ar</td>
                      <td>
                        {estCompletementPaye ? (
                          <span className="badge bg-success">Payé</span>
                        ) : estPartiellementPaye ? (
                          <span className="badge bg-warning">Partiel</span>
                        ) : (
                          <span className="badge bg-danger">Non payé</span>
                        )}
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        </div>
      </div>

      <div className="card mb-4">
        <div className="card-body">
          <h5 className="card-title">Historique des paiements</h5>
          <div className="table-responsive">
            <table className="table table-striped table-hover">
              <thead className="table-primary">
                <tr>
                  <th>Date</th>
                  <th>Activité</th>
                  <th>Montant</th>
                  <th>Pour qui</th>
                </tr>
              </thead>
              <tbody>
                {statistiques.paiements && statistiques.paiements.map((paiement) => (
                  <tr key={paiement.id}>
                    <td>{new Date(paiement.daty).toLocaleDateString()}</td>
                    <td>{paiement.description_activite}</td>
                    <td className="text-success">{parseFloat(paiement.montant).toLocaleString()} Ar</td>
                    <td>
                      {paiement.pour_personne ? (
                        <span className="badge bg-secondary">
                          Pour {paiement.nom_personne} {paiement.prenom_personne}
                        </span>
                      ) : (
                        <span className="badge bg-primary">Pour lui-même</span>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
              <tfoot className="table-secondary fw-bold">
                <tr>
                  <td colSpan="2">Total</td>
                  <td className="text-success">{statistiques.montantRecu?.toLocaleString()} Ar</td>
                  <td></td>
                </tr>
              </tfoot>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MembreStatDetails;
