import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';

const RemisesForm = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    remise1: 5,  // Remise pour 2 personnes (%)
    remise2: 10, // Remise pour 3 personnes (%)
    remise3: 15, // Remise pour 4 personnes (%)
    remise4: 20, // Remise pour 5 personnes ou plus (%)
  });

  // Fonction pour afficher une notification de succès
  const showSuccess = (message) => {
    toast.success(message, {
      position: "top-right",
      autoClose: 3000,
      hideProgressBar: false,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
    });
  };

  // Fonction pour afficher une notification d'erreur
  const showError = (message) => {
    toast.error(message, {
      position: "top-right",
      autoClose: 5000,
      hideProgressBar: false,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
    });
  };

  // Charger les paramètres de remise depuis le localStorage au chargement du composant
  useEffect(() => {
    const savedRemises = localStorage.getItem('remises');
    if (savedRemises) {
      setFormData(JSON.parse(savedRemises));
    }
  }, []);

  // Gérer les changements dans le formulaire
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: parseFloat(value)
    });
  };

  // Soumettre le formulaire
  const handleSubmit = (e) => {
    e.preventDefault();

    try {
      setLoading(true);

      // Vérifier que les valeurs sont valides
      if (formData.remise1 < 0 || formData.remise2 < 0 || formData.remise3 < 0 || formData.remise4 < 0) {
        showError('Les remises ne peuvent pas être négatives');
        setLoading(false);
        return;
      }

      if (formData.remise1 > 100 || formData.remise2 > 100 || formData.remise3 > 100 || formData.remise4 > 100) {
        showError('Les remises ne peuvent pas dépasser 100%');
        setLoading(false);
        return;
      }

      // Vérifier que les remises sont croissantes
      if (formData.remise1 > formData.remise2 || formData.remise2 > formData.remise3 || formData.remise3 > formData.remise4) {
        showError('Les remises doivent être croissantes avec le nombre de personnes');
        setLoading(false);
        return;
      }

      // Enregistrer les paramètres dans le localStorage
      localStorage.setItem('remises', JSON.stringify(formData));

      showSuccess('Paramètres de remise enregistrés avec succès');
      navigate('/paiements');
    } catch (error) {
      console.error('Erreur lors de l\'enregistrement des paramètres:', error);
      showError('Erreur lors de l\'enregistrement des paramètres');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mt-4">
      <div className="card">
        <div className="card-header bg-primary text-white">
          <h2 className="card-title">Paramétrage des Remises</h2>
        </div>
        <div className="card-body">
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="remise1" className="form-label">Remise pour 2 personnes (%)</label>
              <input
                type="number"
                className="form-control"
                id="remise1"
                name="remise1"
                value={formData.remise1}
                onChange={handleChange}
                min="0"
                max="100"
                step="0.5"
                required
              />
              <small className="text-muted">Pourcentage de remise appliqué lorsqu'un membre amène 1 personne supplémentaire</small>
            </div>

            <div className="mb-3">
              <label htmlFor="remise2" className="form-label">Remise pour 3 personnes (%)</label>
              <input
                type="number"
                className="form-control"
                id="remise2"
                name="remise2"
                value={formData.remise2}
                onChange={handleChange}
                min="0"
                max="100"
                step="0.5"
                required
              />
              <small className="text-muted">Pourcentage de remise appliqué lorsqu'un membre amène 2 personnes supplémentaires</small>
            </div>

            <div className="mb-3">
              <label htmlFor="remise3" className="form-label">Remise pour 4 personnes (%)</label>
              <input
                type="number"
                className="form-control"
                id="remise3"
                name="remise3"
                value={formData.remise3}
                onChange={handleChange}
                min="0"
                max="100"
                step="0.5"
                required
              />
              <small className="text-muted">Pourcentage de remise appliqué lorsqu'un membre amène 3 personnes supplémentaires</small>
            </div>

            <div className="mb-3">
              <label htmlFor="remise4" className="form-label">Remise pour 5 personnes ou plus (%)</label>
              <input
                type="number"
                className="form-control"
                id="remise4"
                name="remise4"
                value={formData.remise4}
                onChange={handleChange}
                min="0"
                max="100"
                step="0.5"
                required
              />
              <small className="text-muted">Pourcentage de remise appliqué lorsqu'un membre amène 4 personnes supplémentaires ou plus</small>
            </div>

            <div className="d-flex justify-content-between">
              <button
                type="button"
                className="btn btn-secondary"
                onClick={() => navigate('/paiements')}
                disabled={loading}
              >
                Annuler
              </button>
              <button
                type="submit"
                className="btn btn-primary"
                disabled={loading}
              >
                {loading ? (
                  <>
                    <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                    Enregistrement...
                  </>
                ) : (
                  'Enregistrer'
                )}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default RemisesForm;
