import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Table, Button, Card, Spinner, Alert } from 'react-bootstrap';
import { FaEdit, FaTrash, FaPlus } from 'react-icons/fa';
import remiseService from '../../services/remise.service';
import { useToast } from '../../contexts/ToastContext';

const RemisesList = () => {
  const [remises, setRemises] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { showSuccess, showError } = useToast();

  useEffect(() => {
    loadRemises();
  }, []);

  const loadRemises = async () => {
    try {
      setLoading(true);
      const data = await remiseService.getAllRemises();
      setRemises(data);
      setError(null);
    } catch (err) {
      console.error('Erreur lors du chargement des paramètres de remise:', err);
      setError('Erreur lors du chargement des paramètres de remise');
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('Êtes-vous sûr de vouloir supprimer ce paramètre de remise ?')) {
      try {
        await remiseService.deleteRemise(id);
        showSuccess('Paramètre de remise supprimé avec succès');
        loadRemises();
      } catch (err) {
        console.error('Erreur lors de la suppression du paramètre de remise:', err);
        showError('Erreur lors de la suppression du paramètre de remise');
      }
    }
  };

  if (loading) {
    return (
      <div className="text-center my-5">
        <Spinner animation="border" role="status">
          <span className="visually-hidden">Chargement...</span>
        </Spinner>
      </div>
    );
  }

  return (
    <div className="container my-4">
      <Card>
        <Card.Header className="d-flex justify-content-between align-items-center">
          <h2>Paramètres de Remise</h2>
          <Link to="/remises/new" className="btn btn-primary">
            <FaPlus className="me-2" /> Nouveau paramètre
          </Link>
        </Card.Header>
        <Card.Body>
          {error && <Alert variant="danger">{error}</Alert>}

          {remises.length === 0 ? (
            <Alert variant="info">Aucun paramètre de remise n'a été défini.</Alert>
          ) : (
            <Table striped bordered hover responsive>
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Nombre de personnes</th>
                  <th>Pourcentage de remise</th>
                  <th className="text-center">Actions</th>
                </tr>
              </thead>
              <tbody>
                {remises.map((remise) => (
                  <tr key={remise.id}>
                    <td>{remise.id}</td>
                    <td>{remise.nb_personnes}</td>
                    <td>{remise.pourcentage}%</td>
                    <td className="text-center">
                      <div className="btn-group btn-group-sm">
                        <Link to={`/remises/edit/${remise.id}`} className="btn btn-primary">
                          <FaEdit /> Modifier
                        </Link>
                        <Button variant="danger" onClick={() => handleDelete(remise.id)}>
                          <FaTrash /> Supprimer
                        </Button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          )}
        </Card.Body>
        <Card.Footer>
          <p className="text-muted mb-0">
            Les remises sont appliquées en fonction du nombre de personnes amenées par un membre.
            <br />
            Le pourcentage de remise est appliqué sur le montant total du paiement.
          </p>
        </Card.Footer>
      </Card>
    </div>
  );
};

export default RemisesList;
