import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { Container, Row, Col, Card, Form, Button, Table, Alert, Badge } from 'react-bootstrap';
import membreService from '../../services/membre.service';
import Spinner from '../../components/Spinner';

const MembreSituation = () => {
  const { id } = useParams();
  const [membre, setMembre] = useState(null);
  const [dateDebut, setDateDebut] = useState('');
  const [dateFin, setDateFin] = useState('');
  const [situation, setSituation] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Charger les informations du membre
  useEffect(() => {
    const fetchMembre = async () => {
      try {
        const data = await membreService.getMembreById(id);
        setMembre(data);

        // Initialiser les dates par défaut (dernier mois)
        const today = new Date();
        const lastMonth = new Date();
        lastMonth.setMonth(today.getMonth() - 1);

        setDateFin(formatDate(today));
        setDateDebut(formatDate(lastMonth));
      } catch (err) {
        setError('Erreur lors du chargement des données du membre');
        console.error(err);
      }
    };

    fetchMembre();
  }, [id]);

  // Formater une date au format YYYY-MM-DD
  const formatDate = (date) => {
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, '0');
    const day = String(date.getDate()).padStart(2, '0');
    return `${year}-${month}-${day}`;
  };

  // Formater une date pour l'affichage
  const formatDisplayDate = (dateString) => {
    const date = new Date(dateString);
    const day = String(date.getDate()).padStart(2, '0');
    const month = String(date.getMonth() + 1).padStart(2, '0');
    const year = date.getFullYear();
    return `${day}/${month}/${year}`;
  };

  // Récupérer la situation des activités
  const fetchSituation = async () => {
    if (!dateDebut || !dateFin) {
      setError('Veuillez sélectionner une période');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const data = await membreService.getSituationActivites(id, dateDebut, dateFin);
      setSituation(data);
    } catch (err) {
      setError('Erreur lors du chargement de la situation des activités');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  // Gérer la soumission du formulaire
  const handleSubmit = (e) => {
    e.preventDefault();
    fetchSituation();
  };

  if (!membre) {
    return <Spinner />;
  }

  return (
    <Container className="py-4">
      <Row className="mb-4">
        <Col>
          <h2>
            Situation des activités - {membre.nom} {membre.prenom}
          </h2>
          <p className="text-muted">
            Membre depuis le {formatDisplayDate(membre.date_affiliation)}
          </p>
        </Col>
      </Row>

      <Card className="mb-4">
        <Card.Body>
          <Card.Title>Sélectionner une période</Card.Title>
          <Form onSubmit={handleSubmit}>
            <Row>
              <Col md={4}>
                <Form.Group className="mb-3">
                  <Form.Label>Date de début</Form.Label>
                  <Form.Control
                    type="date"
                    value={dateDebut}
                    onChange={(e) => setDateDebut(e.target.value)}
                    required
                  />
                </Form.Group>
              </Col>
              <Col md={4}>
                <Form.Group className="mb-3">
                  <Form.Label>Date de fin</Form.Label>
                  <Form.Control
                    type="date"
                    value={dateFin}
                    onChange={(e) => setDateFin(e.target.value)}
                    required
                  />
                </Form.Group>
              </Col>
              <Col md={4} className="d-flex align-items-end">
                <Button type="submit" variant="primary" className="mb-3" disabled={loading}>
                  {loading ? 'Chargement...' : 'Afficher la situation'}
                </Button>
              </Col>
            </Row>
          </Form>
        </Card.Body>
      </Card>

      {error && <Alert variant="danger">{error}</Alert>}

      {situation && (
        <>
          <Card className="mb-4">
            <Card.Body>
              <Card.Title>Résumé de la situation</Card.Title>
              <Row>
                <Col md={3}>
                  <div className="border rounded p-3 text-center mb-3">
                    <h3>{situation.nombre_activites}</h3>
                    <p className="mb-0 text-muted">Activités</p>
                  </div>
                </Col>
                <Col md={3}>
                  <div className="border rounded p-3 text-center mb-3">
                    <h3>{situation.nombre_presences}</h3>
                    <p className="mb-0 text-muted">Présences</p>
                  </div>
                </Col>
                <Col md={3}>
                  <div className="border rounded p-3 text-center mb-3">
                    <h3>{situation.montant_a_payer.toLocaleString()} Ar</h3>
                    <p className="mb-0 text-muted">Montant à payer</p>
                  </div>
                </Col>
                <Col md={3}>
                  <div className="border rounded p-3 text-center mb-3">
                    <h3>{situation.montant_recu.toLocaleString()} Ar</h3>
                    <p className="mb-0 text-muted">Montant reçu</p>
                  </div>
                </Col>
              </Row>
              <Row>
                <Col>
                  <div className="border rounded p-3 text-center">
                    <h3 className={situation.montant_a_payer - situation.montant_recu > 0 ? 'text-danger' : 'text-success'}>
                      {(situation.montant_a_payer - situation.montant_recu).toLocaleString()} Ar
                    </h3>
                    <p className="mb-0 text-muted">Reste à payer</p>
                  </div>
                </Col>
              </Row>
            </Card.Body>
          </Card>

          <Card className="mb-4">
            <Card.Body>
              <Card.Title>Liste des activités</Card.Title>
              {situation.activites.length > 0 ? (
                <Table responsive striped hover>
                  <thead>
                    <tr>
                      <th>Date</th>
                      <th>Description</th>
                      <th>Région</th>
                      <th>Cotisation</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {situation.activites.map((activite) => (
                      <tr key={activite.id}>
                        <td>{formatDisplayDate(activite.daty)}</td>
                        <td>{activite.description}</td>
                        <td>
                          <Badge bg="info">{activite.region}</Badge>
                        </td>
                        <td>{activite.cotisation.toLocaleString()} Ar</td>
                        <td>
                          <Link to={`/activites/${activite.id}`} className="btn btn-sm btn-outline-info">
                            Détails
                          </Link>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </Table>
              ) : (
                <Alert variant="info">Aucune activité trouvée pour cette période</Alert>
              )}
            </Card.Body>
          </Card>

          <Card className="mb-4">
            <Card.Body>
              <Card.Title>Liste des paiements</Card.Title>
              {situation.paiements.length > 0 ? (
                <Table responsive striped hover>
                  <thead>
                    <tr>
                      <th>Date</th>
                      <th>Activité</th>
                      <th>Montant</th>
                    </tr>
                  </thead>
                  <tbody>
                    {situation.paiements.map((paiement) => (
                      <tr key={paiement.id}>
                        <td>{formatDisplayDate(paiement.daty)}</td>
                        <td>{paiement.activite_description}</td>
                        <td className="text-success">{paiement.montant.toLocaleString()} Ar</td>
                      </tr>
                    ))}
                  </tbody>
                </Table>
              ) : (
                <Alert variant="info">Aucun paiement trouvé pour cette période</Alert>
              )}
            </Card.Body>
          </Card>
        </>
      )}

      <div className="d-flex justify-content-between">
        <Link to={`/membres/${id}`} className="btn btn-outline-secondary">
          Retour au profil
        </Link>
        <Link to="/membres" className="btn btn-outline-primary">
          Liste des membres
        </Link>
      </div>
    </Container>
  );
};

export default MembreSituation;
