import axios from 'axios';
import API_BASE_URL from './api.config';

const API_URL = `${API_BASE_URL}/remises`;

// Récupérer tous les paramètres de remise
const getAllRemises = async () => {
  try {
    const response = await axios.get(API_URL);
    return response.data;
  } catch (error) {
    throw error;
  }
};

// Récupérer un paramètre de remise par ID
const getRemiseById = async (id) => {
  try {
    const response = await axios.get(`${API_URL}/${id}`);
    return response.data;
  } catch (error) {
    throw error;
  }
};

// Créer un nouveau paramètre de remise
const createRemise = async (remiseData) => {
  try {
    const response = await axios.post(API_URL, remiseData);
    return response.data;
  } catch (error) {
    throw error;
  }
};

// Mettre à jour un paramètre de remise
const updateRemise = async (id, remiseData) => {
  try {
    const response = await axios.put(`${API_URL}/${id}`, remiseData);
    return response.data;
  } catch (error) {
    throw error;
  }
};

// Supprimer un paramètre de remise
const deleteRemise = async (id) => {
  try {
    await axios.delete(`${API_URL}/${id}`);
    return true;
  } catch (error) {
    throw error;
  }
};

// Calculer une remise
const calculerRemise = async (nbPersonnes, montantBase) => {
  try {
    const response = await axios.post(`${API_URL}/calculer`, {
      nb_personnes: nbPersonnes,
      montant_base: montantBase
    });
    return response.data;
  } catch (error) {
    throw error;
  }
};

const remiseService = {
  getAllRemises,
  getRemiseById,
  createRemise,
  updateRemise,
  deleteRemise,
  calculerRemise
};

export default remiseService;
