import axios from 'axios';
import API_BASE_URL from './api.config';

const API_URL = `${API_BASE_URL}/statistiques`;

/**
 * Récupère les statistiques globales des activités des membres
 * (Pour le moment, cette fonction simule les données car l'API n'existe pas encore)
 */
const getStatistiquesGlobales = async () => {
  try {
    // Simulation de données pour le tableau de bord
    // Dans une version future, cela pourrait être remplacé par un appel API réel
    return {
      activites: {
        total: 45,
        moisCourant: 8,
        totalPaiements: 1250000
      },
      membres: {
        total: 32,
        actifs: 28,
        nouveauxMois: 5
      },
      presences: {
        total: 320,
        moyenneParActivite: 12,
        tauxParticipation: 75
      },
      finances: {
        totalAPayer: 2500000,
        totalPaye: 1850000,
        resteAPayer: 650000,
        tauxRecouvrement: 74
      }
    };
  } catch (error) {
    throw error;
  }
};

const statistiquesService = {
  getStatistiquesGlobales
};

export default statistiquesService;
