import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import activiteService from '../services/activite.service';
import membreService from '../services/membre.service';
import Spinner from '../components/common/Spinner';
import Alert from '../components/common/Alert';

const Dashboard = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activites, setActivites] = useState([]);
  const [membres, setMembres] = useState([]);
  const [stats, setStats] = useState({
    totalActivites: 0,
    totalMembres: 0,
    prochainesActivites: 0,
    activitesParRegion: {}
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);

        // Récupérer les données des activités et des membres
        const [activitesData, membresData] = await Promise.all([
          activiteService.getAllActivites(),
          membreService.getAllMembres()
        ]);

        setActivites(activitesData);
        setMembres(membresData);

        // Calculer les statistiques
        const today = new Date();
        const prochainesActivites = activitesData.filter(act => new Date(act.daty) >= today);

        // Regrouper les activités par région
        const activitesParRegion = activitesData.reduce((acc, act) => {
          acc[act.region] = (acc[act.region] || 0) + 1;
          return acc;
        }, {});

        setStats({
          totalActivites: activitesData.length,
          totalMembres: membresData.length,
          prochainesActivites: prochainesActivites.length,
          activitesParRegion
        });

        setLoading(false);
      } catch (err) {
        console.error('Erreur lors du chargement des données:', err);
        setError('Erreur lors du chargement des données. Veuillez réessayer plus tard.');
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // Récupérer les 5 prochaines activités
  const prochainesActivites = [...activites]
    .filter(act => new Date(act.daty) >= new Date())
    .sort((a, b) => new Date(a.daty) - new Date(b.daty))
    .slice(0, 5);

  // Récupérer les 5 derniers membres inscrits
  const derniersMembres = [...membres]
    .sort((a, b) => new Date(b.date_affiliation) - new Date(a.date_affiliation))
    .slice(0, 5);

  if (loading) return <Spinner text="Chargement du tableau de bord..." />;

  return (
    <div className="dashboard">
      <h1 className="mb-4">Tableau de bord STK</h1>

      {error && <Alert type="danger" message={error} />}

      <div className="row mb-4">
        <div className="col-md-3 mb-3">
          <div className="card bg-primary text-white">
            <div className="card-body">
              <h5 className="card-title">Membres</h5>
              <h2 className="card-text">{stats.totalMembres}</h2>
              <Link to="/membres" className="text-white">Voir tous les membres</Link>
            </div>
          </div>
        </div>

        <div className="col-md-3 mb-3">
          <div className="card bg-success text-white">
            <div className="card-body">
              <h5 className="card-title">Activités</h5>
              <h2 className="card-text">{stats.totalActivites}</h2>
              <Link to="/activites" className="text-white">Voir toutes les activités</Link>
            </div>
          </div>
        </div>

        <div className="col-md-3 mb-3">
          <div className="card bg-info text-white">
            <div className="card-body">
              <h5 className="card-title">Prochaines activités</h5>
              <h2 className="card-text">{stats.prochainesActivites}</h2>
              <Link to="/activites" className="text-white">Voir le calendrier</Link>
            </div>
          </div>
        </div>

        <div className="col-md-3 mb-3">
          <div className="card bg-warning text-dark">
            <div className="card-body">
              <h5 className="card-title">Actions rapides</h5>
              <div className="d-grid gap-2">
                <Link to="/activites/new" className="btn btn-sm btn-dark">Nouvelle activité</Link>
                <Link to="/personnes/new" className="btn btn-sm btn-dark">Nouvelle personne</Link>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="row">
        <div className="col-md-6 mb-4">
          <div className="card">
            <div className="card-header bg-primary text-white">
              <h5 className="mb-0">Prochaines activités</h5>
            </div>
            <div className="card-body">
              {prochainesActivites.length > 0 ? (
                <ul className="list-group list-group-flush">
                  {prochainesActivites.map(activite => (
                    <li key={activite.id} className="list-group-item d-flex justify-content-between align-items-center">
                      <div>
                        <strong>{new Date(activite.daty).toLocaleDateString()}</strong> - {activite.description}
                        <span className="badge bg-secondary ms-2">{activite.region}</span>
                      </div>
                      <Link to={`/activites/${activite.id}`} className="btn btn-sm btn-outline-primary">
                        Détails
                      </Link>
                    </li>
                  ))}
                </ul>
              ) : (
                <p className="text-muted">Aucune activité à venir</p>
              )}
              <div className="mt-3">
                <Link to="/activites" className="btn btn-primary">Voir toutes les activités</Link>
              </div>
            </div>
          </div>
        </div>

        <div className="col-md-6 mb-4">
          <div className="card">
            <div className="card-header bg-success text-white">
              <h5 className="mb-0">Derniers membres inscrits</h5>
            </div>
            <div className="card-body">
              {derniersMembres.length > 0 ? (
                <ul className="list-group list-group-flush">
                  {derniersMembres.map(membre => (
                    <li key={membre.id} className="list-group-item d-flex justify-content-between align-items-center">
                      <div>
                        {membre.nom} {membre.prenom}
                        <small className="text-muted ms-2">
                          Inscrit le {new Date(membre.date_affiliation).toLocaleDateString()}
                        </small>
                      </div>
                      <Link to={`/membres/edit/${membre.id}`} className="btn btn-sm btn-outline-success">
                        Profil
                      </Link>
                    </li>
                  ))}
                </ul>
              ) : (
                <p className="text-muted">Aucun membre inscrit</p>
              )}
              <div className="mt-3">
                <Link to="/membres" className="btn btn-success">Voir tous les membres</Link>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="row">
        <div className="col-md-12 mb-4">
          <div className="card">
            <div className="card-header bg-info text-white">
              <h5 className="mb-0">Activités par région</h5>
            </div>
            <div className="card-body">
              <div className="row">
                {Object.entries(stats.activitesParRegion).map(([region, count]) => (
                  <div key={region} className="col-md-3 mb-3">
                    <div className="card">
                      <div className="card-body text-center">
                        <h5 className="card-title">{region}</h5>
                        <h3 className="card-text">{count}</h3>
                        <p className="card-text">activités</p>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
