import React, { useState, useEffect } from 'react';
import statistiqueService from '../../services/statistique.service';
import Spinner from '../../components/common/Spinner';
import Alert from '../../components/common/Alert';

const SituationActivitesMembres = () => {
  const [dateDebut, setDateDebut] = useState('');
  const [dateFin, setDateFin] = useState('');
  const [statistiques, setStatistiques] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Initialiser les dates par défaut (mois en cours)
  useEffect(() => {
    const now = new Date();
    const firstDayOfMonth = new Date(now.getFullYear(), now.getMonth(), 1);
    const lastDayOfMonth = new Date(now.getFullYear(), now.getMonth() + 1, 0);

    setDateDebut(firstDayOfMonth.toISOString().split('T')[0]);
    setDateFin(lastDayOfMonth.toISOString().split('T')[0]);
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!dateDebut || !dateFin) {
      setError('Veuillez sélectionner les dates de début et de fin');
      return;
    }

    try {
      setLoading(true);
      setError(null);

      const data = await statistiqueService.getStatistiquesByDateRange(dateDebut, dateFin);
      setStatistiques(data);

      setLoading(false);
    } catch (err) {
      console.error('Erreur lors de la récupération des statistiques:', err);
      setError('Erreur lors de la récupération des statistiques. Veuillez réessayer plus tard.');
      setLoading(false);
    }
  };

  return (
    <div className="container-fluid mt-4">
      <h2 className="mb-4">Situation des Activités et Membres</h2>

      {error && <Alert type="danger" message={error} onClose={() => setError(null)} />}

      <div className="card mb-4">
        <div className="card-body">
          <h5 className="card-title">Sélectionner la période</h5>
          <form onSubmit={handleSubmit}>
            <div className="row g-3 align-items-end">
              <div className="col-md-4">
                <label htmlFor="dateDebut" className="form-label">Date de début</label>
                <input
                  type="date"
                  className="form-control"
                  id="dateDebut"
                  value={dateDebut}
                  onChange={(e) => setDateDebut(e.target.value)}
                  required
                />
              </div>
              <div className="col-md-4">
                <label htmlFor="dateFin" className="form-label">Date de fin</label>
                <input
                  type="date"
                  className="form-control"
                  id="dateFin"
                  value={dateFin}
                  onChange={(e) => setDateFin(e.target.value)}
                  required
                />
              </div>
              <div className="col-md-4">
                <button type="submit" className="btn btn-primary w-100">
                  <i className="bi bi-search me-2"></i>
                  Rechercher
                </button>
              </div>
            </div>
          </form>
        </div>
      </div>

      {loading ? (
        <Spinner text="Chargement des statistiques..." />
      ) : statistiques ? (
        <div className="row mb-4">
          <div className="col-12">
            <div className="alert alert-info">
              <strong>Période:</strong> Du {new Date(statistiques.dateDebut).toLocaleDateString()} au {new Date(statistiques.dateFin).toLocaleDateString()}
            </div>
          </div>
        </div>
      ) : null}

      {statistiques && (
        <>
          <div className="card mb-4">
            <div className="card-body">
              <h5 className="card-title">Résumé financier</h5>
              <div className="progress mb-3" style={{ height: '25px' }}>
                <div 
                  className="progress-bar bg-success" 
                  role="progressbar" 
                  style={{ 
                    width: `${statistiques.montantTotalAPayer > 0 ? 
                      (statistiques.montantTotalRecu / statistiques.montantTotalAPayer) * 100 : 0}%` 
                  }}
                  aria-valuenow={statistiques.montantTotalRecu} 
                  aria-valuemin="0" 
                  aria-valuemax={statistiques.montantTotalAPayer}
                >
                  {statistiques.montantTotalAPayer > 0 ? 
                    `${Math.round((statistiques.montantTotalRecu / statistiques.montantTotalAPayer) * 100)}%` : '0%'}
                </div>
              </div>
              <div className="row">
                <div className="col-md-6">
                  <p className="mb-1">
                    <strong>Reste à payer:</strong> {Math.max(0, statistiques.montantTotalAPayer - statistiques.montantTotalRecu).toLocaleString()} Ar
                  </p>
                  <p className="mb-1">
                    <strong>Taux de recouvrement:</strong> {statistiques.montantTotalAPayer > 0 ? 
                      `${Math.round((statistiques.montantTotalRecu / statistiques.montantTotalAPayer) * 100)}%` : '0%'}
                  </p>
                </div>
                <div className="col-md-6">
                  <p className="mb-1">
                    <strong>Moyenne par activité:</strong> {statistiques.nombreActivites > 0 ? 
                      `${Math.round(statistiques.nombrePresences / statistiques.nombreActivites)} participants` : '0 participant'}
                  </p>
                  <p className="mb-1">
                    <strong>Moyenne par présence:</strong> {statistiques.nombrePresences > 0 ? 
                      `${Math.round(statistiques.montantTotalRecu / statistiques.nombrePresences).toLocaleString()} Ar` : '0 Ar'}
                  </p>
                </div>
              </div>
            </div>
          </div>

          {/* Tableau des statistiques par région */}
          <div className="card mb-4">
            <div className="card-body">
              <h5 className="card-title">Statistiques par région</h5>
              <div className="table-responsive">
                <table className="table table-striped table-hover">
                  <thead className="table-primary">
                    <tr>
                      <th>Région</th>
                      <th>Nombre d'activités</th>
                      <th>Nombre de présences</th>
                      <th>Montant à payer</th>
                      <th>Montant reçu</th>
                      <th>Reste à payer</th>
                      <th>Taux de recouvrement</th>
                    </tr>
                  </thead>
                  <tbody>
                    {statistiques.statistiquesParRegion && statistiques.statistiquesParRegion.map((region, index) => {
                      const montantAPayer = parseFloat(region.montant_a_payer || 0);
                      const montantRecu = parseFloat(region.montant_recu || 0);
                      const resteAPayer = Math.max(0, montantAPayer - montantRecu);
                      const tauxRecouvrement = montantAPayer > 0 ? (montantRecu / montantAPayer) * 100 : 0;

                      return (
                        <tr key={index}>
                          <td>{region.region}</td>
                          <td>{region.nombre_activites}</td>
                          <td>{region.nombre_presences}</td>
                          <td>{montantAPayer.toLocaleString()} Ar</td>
                          <td className="text-success">{montantRecu.toLocaleString()} Ar</td>
                          <td className="text-danger">{resteAPayer.toLocaleString()} Ar</td>
                          <td>
                            <div className="progress" style={{ height: '20px' }}>
                              <div 
                                className="progress-bar bg-success" 
                                role="progressbar" 
                                style={{ width: `${tauxRecouvrement}%` }}
                                aria-valuenow={tauxRecouvrement} 
                                aria-valuemin="0" 
                                aria-valuemax="100"
                              >
                                {Math.round(tauxRecouvrement)}%
                              </div>
                            </div>
                          </td>
                        </tr>
                      );
                    })}
                  </tbody>
                </table>
              </div>
            </div>
          </div>

          {/* Tableau des statistiques par activité */}
          <div className="card mb-4">
            <div className="card-body">
              <h5 className="card-title">Statistiques par activité</h5>
              <div className="table-responsive">
                <table className="table table-striped table-hover">
                  <thead className="table-primary">
                    <tr>
                      <th>Date</th>
                      <th>Description</th>
                      <th>Région</th>
                      <th>Cotisation</th>
                      <th>Participants</th>
                      <th>Total à payer</th>
                      <th>Montant reçu</th>
                      <th>Reste à payer</th>
                      <th>Taux</th>
                    </tr>
                  </thead>
                  <tbody>
                    {statistiques.statistiquesParActivite && statistiques.statistiquesParActivite.map((activite) => {
                      const cotisation = parseFloat(activite.cotisation || 0);
                      const nombreParticipants = parseInt(activite.nombre_participants || 0);
                      const totalAPayer = parseFloat(activite.total_a_payer || 0);
                      const montantRecu = parseFloat(activite.montant_recu || 0);
                      const resteAPayer = parseFloat(activite.reste_a_payer || 0);
                      const tauxRecouvrement = totalAPayer > 0 ? (montantRecu / totalAPayer) * 100 : 0;

                      return (
                        <tr key={activite.id}>
                          <td>{new Date(activite.daty).toLocaleDateString()}</td>
                          <td>{activite.description}</td>
                          <td>{activite.region}</td>
                          <td>{cotisation.toLocaleString()} Ar</td>
                          <td>{nombreParticipants}</td>
                          <td>{totalAPayer.toLocaleString()} Ar</td>
                          <td className="text-success">{montantRecu.toLocaleString()} Ar</td>
                          <td className="text-danger">{resteAPayer.toLocaleString()} Ar</td>
                          <td>
                            <div className="progress" style={{ height: '20px' }}>
                              <div 
                                className="progress-bar bg-success" 
                                role="progressbar" 
                                style={{ width: `${tauxRecouvrement}%` }}
                                aria-valuenow={tauxRecouvrement} 
                                aria-valuemin="0" 
                                aria-valuemax="100"
                              >
                                {Math.round(tauxRecouvrement)}%
                              </div>
                            </div>
                          </td>
                        </tr>
                      );
                    })}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default SituationActivitesMembres;
