import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import membreService from '../../services/membre.service';
import Spinner from '../../components/common/Spinner';
import Alert from '../../components/common/Alert';

const MembresList = () => {
  const [membres, setMembres] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  // États pour les statistiques d'activités
  const [showStats, setShowStats] = useState(false);
  const [dateDebut, setDateDebut] = useState(new Date(new Date().getFullYear(), 0, 1).toISOString().split('T')[0]); // 1er janvier de l'année en cours
  const [dateFin, setDateFin] = useState(new Date().toISOString().split('T')[0]); // Aujourd'hui
  const [statsLoading, setStatsLoading] = useState(false);
  const [membresStats, setMembresStats] = useState([]);

  useEffect(() => {
    fetchMembres();
  }, []);

  const fetchMembres = async () => {
    try {
      setLoading(true);
      const data = await membreService.getAllMembres();
      setMembres(data);
      setLoading(false);
    } catch (err) {
      console.error('Erreur lors du chargement des membres:', err);
      setError('Erreur lors du chargement des membres. Veuillez réessayer plus tard.');
      setLoading(false);
    }
  };

  // Fonction pour récupérer les statistiques d'activités des membres entre deux dates
  const fetchMembresStats = async () => {
    try {
      setStatsLoading(true);
      setError(null);

      // Vérifier que les dates sont valides
      if (!dateDebut || !dateFin) {
        setError('Veuillez sélectionner une date de début et une date de fin.');
        setStatsLoading(false);
        return;
      }

      // Vérifier que la date de début est antérieure à la date de fin
      if (new Date(dateDebut) > new Date(dateFin)) {
        setError('La date de début doit être antérieure à la date de fin.');
        setStatsLoading(false);
        return;
      }

      // Récupérer les statistiques
      const stats = await membreService.getAllMembresActivitesStats(dateDebut, dateFin);

      // Fusionner les statistiques avec les données des membres
      const membresAvecStats = membres.map(membre => {
        const membreStat = stats.find(stat => stat.id_membre === membre.id) || {
          nombre_activites: 0,
          nombre_presences: 0,
          montant_a_payer: 0,
          montant_paye: 0
        };

        return {
          ...membre,
          stats: membreStat
        };
      });

      setMembresStats(membresAvecStats);
      setShowStats(true);
      setStatsLoading(false);
    } catch (err) {
      console.error('Erreur lors du chargement des statistiques:', err);
      setError('Erreur lors du chargement des statistiques. Veuillez réessayer plus tard.');
      setStatsLoading(false);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('Êtes-vous sûr de vouloir supprimer ce membre ? Cette action ne supprimera pas la personne de la base de données.')) {
      try {
        await membreService.deleteMembre(id);
        setMembres(membres.filter(membre => membre.id !== id));
        setSuccessMessage('Membre supprimé avec succès');

        // Effacer le message de succès après 3 secondes
        setTimeout(() => {
          setSuccessMessage('');
        }, 3000);
      } catch (err) {
        console.error('Erreur lors de la suppression du membre:', err);
        setError('Erreur lors de la suppression du membre. Veuillez réessayer plus tard.');
      }
    }
  };

  const filteredMembres = membres.filter(membre => {
    const fullName = `${membre.nom} ${membre.prenom}`.toLowerCase();
    return fullName.includes(searchTerm.toLowerCase());
  });

  if (loading) return <Spinner text="Chargement des membres..." />;

  return (
    <div className="membres-list">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h1>Liste des membres</h1>
        <Link to="/membres/new" className="btn btn-primary">
          <i className="bi bi-plus-circle me-2"></i>Ajouter un membre
        </Link>
      </div>

      {successMessage && (
        <Alert 
          type="success" 
          message={successMessage} 
          onClose={() => setSuccessMessage('')}
        />
      )}

      {error && (
        <Alert 
          type="danger" 
          message={error} 
          onClose={() => setError(null)}
        />
      )}

      <div className="card mb-4">
        <div className="card-body">
          <div className="row mb-3">
            <div className="col-md-12">
              <div className="input-group">
                <span className="input-group-text">
                  <i className="bi bi-search"></i>
                </span>
                <input
                  type="text"
                  className="form-control"
                  placeholder="Rechercher un membre..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
                {searchTerm && (
                  <button
                    className="btn btn-outline-secondary"
                    type="button"
                    onClick={() => setSearchTerm('')}
                  >
                    <i className="bi bi-x"></i>
                  </button>
                )}
              </div>
            </div>
          </div>

          <div className="row">
            <div className="col-12">
              <h6 className="mb-3">Situation activités membres (entre 2 dates)</h6>
            </div>
            <div className="col-md-4">
              <div className="form-group mb-3">
                <label className="form-label">Date de début</label>
                <input
                  type="date"
                  className="form-control"
                  value={dateDebut}
                  onChange={(e) => setDateDebut(e.target.value)}
                />
              </div>
            </div>
            <div className="col-md-4">
              <div className="form-group mb-3">
                <label className="form-label">Date de fin</label>
                <input
                  type="date"
                  className="form-control"
                  value={dateFin}
                  onChange={(e) => setDateFin(e.target.value)}
                />
              </div>
            </div>
            <div className="col-md-4 d-flex align-items-end">
              <button 
                className="btn btn-primary mb-3" 
                onClick={fetchMembresStats}
                disabled={statsLoading}
              >
                {statsLoading ? (
                  <>
                    <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                    Chargement...
                  </>
                ) : (
                  <>Afficher les statistiques</>
                )}
              </button>
              {showStats && (
                <button 
                  className="btn btn-outline-secondary mb-3 ms-2" 
                  onClick={() => setShowStats(false)}
                >
                  Masquer les statistiques
                </button>
              )}
            </div>
          </div>
        </div>
      </div>

      {filteredMembres.length === 0 ? (
        <div className="alert alert-info">
          Aucun membre trouvé.
        </div>
      ) : (
        <div className="table-responsive">
          <table className="table table-striped table-hover">
            <thead className="table-light">
              <tr>
                <th>ID</th>
                <th>Nom</th>
                <th>Prénom</th>
                <th>Date de naissance</th>
                <th>Date d'affiliation</th>
                <th>Service Public</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredMembres.map(membre => (
                <tr key={membre.id}>
                  <td>{membre.id}</td>
                  <td>{membre.nom}</td>
                  <td>{membre.prenom}</td>
                  <td>{new Date(membre.dtn).toLocaleDateString()}</td>
                  <td>{new Date(membre.date_affiliation).toLocaleDateString()}</td>
                  <td>{membre.sp_description || '-'}</td>
                  <td>
                    <div className="btn-group btn-group-sm">
                      <Link 
                        to={`/membres/edit/${membre.id}`} 
                        className="btn btn-outline-primary"
                        title="Modifier"
                      >
                        <i className="bi bi-pencil"></i>
                      </Link>
                      <button
                        className="btn btn-outline-danger"
                        onClick={() => handleDelete(membre.id)}
                        title="Supprimer"
                      >
                        <i className="bi bi-trash"></i>
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default MembresList;
